import React, { useState, useEffect } from 'react';
import { Collapse, Navbar, NavbarToggler, NavbarBrand, Nav, NavItem, NavLink } from 'reactstrap';
import { Form, FormGroup, Label, Input } from 'reactstrap';
import "./StudentDashboard.css";
import dummyData from './dummyData';

const StudentNav = () => {

    const [collapsed, setCollapsed] = useState(true);

    const toggleNavbar = () => setCollapsed(!collapsed);

    const [searchTerm, setSearchTerm] = useState(""); 

    const [searchResults, setSearchResults] = useState(dummyData)   


    useEffect(() => {

      const results = dummyData.filter((item) => {
        return item.toLowerCase().includes(searchTerm.toLowerCase())
      });

      setSearchResults(results)
    }, [searchTerm]);

    /* STEP 2: create handleChange function and add to input 
     The handleChange method takes the event object as the argument
     and sets the current value of the input to the searchTerm state
     using setSearchTerm
    */
    const handleChange = (event) => {
      setSearchTerm(event.target.value);
    };

    return(

        <div>
        <Navbar color="faded" light>
          <NavbarBrand href="/" className="mr-auto" style={{fontFamily: "Roboto Mono , serif"}}>Dev Desk</NavbarBrand>

          <i className="fa fa-search"></i>
          <div>
          <Form>
          <FormGroup>
        <Label for="searchBar"></Label>
        <Input 
        type="text" 
        name="searchBar" 
        id="searchBar" 
        placeholder="Search"
        onChange={handleChange}
        value={searchTerm} />
        </FormGroup>
        </Form>
        </div>

          <NavbarToggler onClick={toggleNavbar} className="mr-2" />
          <Collapse isOpen={!collapsed} navbar>
            <Nav navbar>
              <NavItem>
                <NavLink href="/new_ticket_form/">Create a new ticket</NavLink>
              </NavItem>
              <NavItem>
                <NavLink href="/student_dashboard">Go to Student Dashboard</NavLink>
              </NavItem>
              <NavItem>
                <NavLink href="/helper_dashboard">Go to Helper Dashboard</NavLink>
              </NavItem>
              <NavItem>
                <NavLink href="/log_out">Log Out</NavLink>
              </NavItem>
            </Nav>
          </Collapse>
        </Navbar>
      </div>
    );

}
export default StudentNav
